SET NAMES utf8mb4;
CREATE DATABASE IF NOT EXISTS job_manage DEFAULT CHARACTER SET utf8mb4;
USE job_manage;

CREATE TABLE IF NOT EXISTS `host_topo`  (
  `host_id` bigint(20) NOT NULL,
  `set_id` bigint(20) NOT NULL,
  `module_id` bigint(20) NOT NULL,
  PRIMARY KEY (`host_id`, `set_id`, `module_id`) USING BTREE,
  INDEX `idx_set_id`(`set_id`) USING BTREE,
  INDEX `idx_module_id`(`module_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci;

